import { useState } from "react";
import { Checkbox, FormField } from "semantic-ui-react";
import { Spot } from "../resources/MapResources";


type SpotPopupProps = {
    spot: Spot;
    isEditing: boolean;
}

const SpotPopup: React.FC<SpotPopupProps> = (props) => {
    const spot = props.spot
    const [checked, setChecked] = useState<boolean>(props.spot.visited);

    const handleToggle = () => {
        setChecked(!checked);
    }

    return(
        props. isEditing? 
            <>
                <FormField
                    fieldName='name'
                    label='Name'
                    value={spot.name}
                />
                <FormField
                    fieldName='recommendations'
                    label='Recommendations'
                    value={spot.name}
                />
                <Checkbox 
                    label={'Visted'} 
                    checked={checked}
                    onClick={handleToggle}
                />
                <p>{`${spot.lat}, ${spot.lon}`}</p>
            </> : 
            <div>
                <h3>spot.name</h3>
                <p><em>{spot.recommendations}</em></p>
                <Checkbox 
                    label={'Visted'} 
                    checked={checked}
                    onClick={handleToggle}
                />
                <p>{`${spot.lat}, ${spot.lon}`}</p>
            </div>
    )
};

export default SpotPopup;
