import L from 'leaflet';
import "leaflet/dist/leaflet.css";
import { MapContainer, Marker, TileLayer, Popup,  } from 'react-leaflet';

import  { ChekitMap, ChekitSpot } from '../resources/MapResources'
import SpotPopup from './SpotPopup';

type FavoritesMapProps = {
    map: ChekitMap;
}

const FavoritesMap: React.FC<FavoritesMapProps> = (props) => {
    //Defalt coordinates set to Denver Union Station

    const icon = L.divIcon({
        className: "spot-icon",
        iconSize: [12, 12],
        iconAnchor: [0, 0],
        popupAnchor: [15, 0]
    });    

    return (
        <>
            <TileLayer 
                attribution="&copy; <a href='http://osm.org/copyright'>OpenStreetMap</a> contributors"
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {
                props.map.layers.map((layer) => {
                    layer.spots.map((item, index) => {
                        <Marker
                            icon={icon}
                            key={index}
                            position={[item.lat, item.lon]}
                            title={`${item.name}`}
                        >
                            <Popup>
                                <SpotPopup
                                    spot={item as ChekitSpot}
                                />
                            </Popup>
                        </Marker>
                })})
            }
        </>    
    );
}

export default FavoritesMap;
