import { useState } from "react";
import { Checkbox, FormField } from "semantic-ui-react";
import { Spot } from "../resources/MapResources";


type SpotPopupProps = {
    spot: Spot;
}

const SpotPopup: React.FC<SpotPopupProps> = (props) => {
    const spot = props.spot
    const [checked, setChecked] = useState<boolean>(props.spot.visited);
    const [isEditing, setEditing] = useState(false);

    const handleToggle = () => {
        setChecked(!checked);
    }

    return(
        isEditing? 
            <>
                <FormField
                    fieldName='description'
                    fieldError={errors.description}
                    label='Additional Description'
                    description={['A pre-set description is automatically included in your Approval notification.', 'Optionally, add any additional information you would like included.']}
                    value={values.description as string}
                />

            </> : 
            <div>
                <h3>spot.name</h3>
                <p><em>{spot.recommendations}</em></p>
                <Checkbox 
                    label={'Visted'} 
                    checked={checked}
                    onClick={handleToggle}
                />
                <p>{`${spot.lat}, ${spot.lon}`}</p>
            </div>
    )
};

export default SpotPopup;
