import { useState } from "react";
import { Button, Checkbox, FormField } from "semantic-ui-react";

import { Spot } from "../resources/MapResources";


type SpotPopupProps = {
    spot: Spot;
}

const SpotPopup: React.FC<SpotPopupProps> = (props) => {
    const { spot }  = props
    const [checked, setChecked] = useState<boolean>(props.spot.visited);
    const [isEditing, setEditing] = useState(false);

    const handleEdit = () => {
        setEditing(!isEditing)
    }

    const handleToggle = () => {
        setChecked(!checked);
    }

    return(
        <div>
         { isEditing? 
            <>
                <FormField
                    fieldName='name'
                    label='Name'
                    value={spot.name}
                />
                <FormField
                    fieldName='recommendations'
                    label='Recommendations'
                    value={spot.name}
                />
                <Checkbox 
                    label={'Visted'} 
                    checked={checked}
                    onClick={handleToggle}
                />
                <p>{`${spot.lat}, ${spot.lon}`}</p>
            </> : 
            <div>
                <h3>spot.name</h3>
                <p><em>{spot.recommendations}</em></p>
                <Checkbox 
                    label={'Visted'} 
                    checked={checked}
                    onClick={handleToggle}
                />
                <p>{`${spot.lat}, ${spot.lon}`}</p>
            </div> 
        }
            <Button label='Edit' onClick={handleEdit} />
        </div>
    )
};

export default SpotPopup;
