import L, { LatLngExpression, map } from 'leaflet';
import "leaflet/dist/leaflet.css";
import { MapContainer, Marker, TileLayer, Popup } from 'react-leaflet';

import  { ChekitMap } from '../resources/MapResources'
import { StockList } from '../resources/constants'
import { useRef } from 'react';

type FavoritesMapProps = {
    map?: ChekitMap;
}

const FavoritesMap: React.FC<FavoritesMapProps> = (props) => {
    //Defalt coordinates set to Denver Union Station
    const mapRef = useRef(null);
    const getMapZoom = () => {
        return mapRef && console.log("object", mapRef.current)
    };


    const position: LatLngExpression = [39.7522, -104.9989];
    const zoom: number = 30;

    const icon: L.DivIcon = L.divIcon({
        className: "spot-icon",
        iconSize: [zoom, zoom],
        iconAnchor: [0, 0],
        popupAnchor: [15, 0],
      });

    return (
        <MapContainer 
            center={position} 
            zoom={zoom} 
            scrollWheelZoom={true}
            ref={mapRef}
            whenReady
        >
            <TileLayer 
                attribution="&copy; <a href='http://osm.org/copyright'>OpenStreetMap</a> contributors"
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {
                StockList.map((item, index) => ( 
                    <Marker
                        icon={icon}
                        key={index}
                        position={[item.lat, item.lon]}
                        title={`${item.name} at ${item.recommendations}`}
                    >
                        <Popup>
                            <strong>
                                {item.name}
                            </strong>
                            <br />
                            <p>
                                {item.recommendations && <em>{item.recommendations}</em>}
                            </p>
                        </Popup>
                    </Marker>
               ))
            }
        </MapContainer>
    );
}

export default FavoritesMap;
