import L, { LatLngExpression, map } from 'leaflet';
import "leaflet/dist/leaflet.css";
import { MapContainer, Marker, TileLayer, Popup, useMap } from 'react-leaflet';

import  { ChekitMap, ChekitSpot } from '../resources/MapResources'
import { StockList } from '../resources/constants'

type FavoritesMapProps = {
    map?: ChekitMap;
}

const FavoritesMap: React.FC<FavoritesMapProps> = (props) => {
    //Defalt coordinates set to Denver Union Station

    const handleIcon = (spot: ChekitSpot) => {
        return L.divIcon({
            iconSize: [12, 12],
            iconAnchor: [0, 0],
            popupAnchor: [15, 0],
          });

    }    

    return (
        <>
            <TileLayer 
                attribution="&copy; <a href='http://osm.org/copyright'>OpenStreetMap</a> contributors"
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {
                StockList.map((item, index) => ( 

                    <Marker
                        icon={handleIcon(item as ChekitSpot)}
                        key={index}
                        position={[item.lat, item.lon]}
                        title={`${item.name}`}
                    >
                        <Popup>
                            <strong>
                                {item.name}
                            </strong>
                            <br />
                            <p>
                                {item.recommendations && <em>{item.recommendations}</em>}
                            </p>
                        </Popup>
                    </Marker>
               ))
            }
            </>    );
}

export default FavoritesMap;
