import { Dropdown, Icon } from 'semantic-ui-react';

type TopMenuProps = {
    mapName: string;
    handleClickMenu: () => void;
    handleClickAdd: () => void;
}

const addOptions = [
    { key: 'spot', text: 'Spot', value: 'spot' },
    { key: 'layer', text: 'Layer', value: 'layer' },
    { key: 'map', text: 'Map', value: 'city' },
]

const menuOptions = [
    { key: 'spot', text: 'Cities', value: 'spot' },
    { key: 'support', text: 'Support', value: 'layer' },
    { key: 'map', text: 'Login', value: 'city' },
]

const TopMenu: React.FC<TopMenuProps> = (props) => {
    const { mapName, handleClickMenu, handleClickAdd } = props;
    return (
        <div className="top-menu">
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.css" />
            <Dropdown
                button
                className='icon'
                floating
                icon={'bars'}
                onChange={handleClickAdd}
            />
            <div className='title'>{mapName}</div>
            <Dropdown
                button
                className='icon'
                floating
                icon={'plus'}
                options={addOptions}
                onChange={handleClickAdd}
            />
        </div>
    )
};

export default TopMenu;
