import { useState } from "react";

import { Spot } from "../resources/MapResources";


type SpotPopupProps = {
    spot: Spot;
}

const SpotPopup: React.FC<SpotPopupProps> = (props) => {
    const { spot }  = props
    const [checked, setChecked] = useState<boolean>(props.spot.visited);
    const [isEditing, setEditing] = useState(false);

    const handleEdit = () => {
        setEditing(!isEditing)
    }

    const handleToggle = () => {
        setChecked(!checked);
    }

    return(
        <div>
            { isEditing? 
                <div className="editForm">
                    <label >Name: </label>
                    <input 
                        type="text" id="name" name="name" value={spot.name}required/>
                    <br />
                    <label >Recommendations: </label>
                    <input 
                        type="text" 
                        id="recommendations" 
                        name="recommendations" 
                        value={spot.recommendations}
                    />
                    <br />
                    <input 
                        type="checkbox" 
                        checked={checked}
                        onClick={handleToggle}
                    />
                    <label >Visted</label>
                    <p>{`${spot.lat}, ${spot.lon}`}</p>
                </div> : 
                <div>
                    <h3>{spot.name}</h3>
                    <p><em>{spot.recommendations}</em></p>
                    <input type="checkbox" 
                        checked={checked}
                        onClick={handleToggle}
                    />
                    <label >Visted</label>
                    <p>{`${spot.lat}, ${spot.lon}`}</p>
                </div> 
            }
            <button onClick={handleEdit}>Edit</button>
        </div>
    )
};

export default SpotPopup;
